# Debug:
setVerbosity(False)

interactive = False # use True to play with keyboard
use_curses = False # Work in progress
printToScreen = True
saveAnimation = False


def look_ahead(vm, moves = [0], bounces = 0):
    temp_vm = copy.deepcopy(vm)
    ball_x = -1
    ballPositions = []
    bricks = 0
    temp_vm.input = list(moves)
    for x,y,id in zip(*[iter(temp_vm.run())]*3):
        temp_vm.input += [0]
        if x == -1 and y == 0 and id > 0: # hackish way to know a brick was hit
            bricks += 1
        elif id == 4:
            ballPositions += [(x,y)]
            if y == 17:
                if bounces:
                    bounces -= 1
                else:
                    ball_x = x
                    break


    return ballPositions, ball_x, bricks


def play_ahead(vm, screen, moves = [], useCopy = True):
    if useCopy:
        vm = copy.deepcopy(vm)
        screen = copy.deepcopy(screen)
    ballVector = []
    bricks = 0
    vm.input = list(moves)
    while vm.is_running:
        try:
            x, y, id = next(vm.run()), next(vm.run()), next(vm.run())
            if x == -1 and y == 0 and id > 0: # hackish way to know a brick was hit
                bricks += 1
            # print(x,y,id, "   ", end='\r')
            # if x == 1 and y == 18 and id == 4:
            #     snapshot(screen, True, False)
            #     print(moves)
            #     print(vm.input)
            #     screen[x,y] = id
            #     snapshot(screen, True, False)
            # if x == 2 and y == 18 and id == 3:
            #     snapshot(screen, True, False)
            #     print(moves)
            #     print(vm.input)
            #     screen[x,y] = id
            #     snapshot(screen, True, False)
            if x == 1 and y == 18 and id == 4: # Bug
                vm = False
                # screen[x,y] = id
                # snapshot(screen)
                # assert False
                break
            if x != -1 or y != 0:
                if id == 4:
                    ballVector += [(x,y)]
                screen[x,y] = id
        except StopIteration:
            if any(b for b in screen.values() if b == 2):
                vm = False
            break
        except NeedInputException:
            break
    return vm, screen, ballVector[-3:], bricks


bestStepNum = 80 # None
bestMoves = None
states = {}
totBranches = 0
totPruned = 0
totExecs = 0

def recurSolve(thisVm, thisScreen, stepNum = 0, moves = []):
    global bestStepNum, bestMoves, states, totBranches, totPruned, totExecs, xMax, xMin
    totExecs += 1
    totBranches += 1
    if totExecs > 5000000:
        return
    print(f"[{totExecs}] totBranches: {totBranches} (states: {len(states)}, pruned: {totPruned}) | recurSolve [{stepNum}].", end="\r")
    if bestStepNum and stepNum >= bestStepNum:
        totBranches -= 1
        return
    ballPositions, ball_x, bricks = look_ahead(thisVm)
    if ball_x == -1:
        totBranches -= 1
        if bricks == 0:
            # assert any(b for b in thisScreen.values() if b == 2)
            return
        bestStepNum = stepNum
        bestMoves = moves
        print("\nwon in", bestStepNum, "steps with:", len(moves), "moves")
        return

    assert(len(ballPositions) >= 2)
    newState = (hash(frozenset(thisScreen.items())), tuple(ballPositions[-2:]))
    if newState in states and states[newState] <= stepNum:
        totPruned += 1
        totBranches -= 1
        return
    states[newState] = stepNum

    paddle_x = [x for x,v in thisScreen.items() if v == 3][0][0]
    ballMoves = len(ballPositions) + 1

    newMovesArr = []
    for dir, pos in itertools.product([-1, 1, 0],[0, -1, 1]):
    # for dir, pos in itertools.product([0, -1, 1],[0, -1, 1]):
        target = ball_x + pos
        dist = abs(paddle_x - target)
        if xMin > target or target > xMax or dist > ballMoves:
            continue
        newMovesArr += [[cmp(target, paddle_x)] * dist + [0]*(ballMoves-dist) + [dir]]

    nextStates = [(newMoves, *play_ahead(thisVm, thisScreen, moves=newMoves, useCopy=(len(newMovesArr) > 1))) for newMoves in newMovesArr]

    # print([x[4] for x in sorted(nextStates, key = lambda x:x[4], reverse = True)])
    for newMoves, newVm, newScreen, _, _ in sorted(nextStates, key = lambda x:x[4], reverse = True):
        if newVm:
            recurSolve(newVm, newScreen, stepNum+1, moves + newMoves)

    totBranches -= 1


# Init game:
instructions = loadCSVInput()
instructions[0] = 2
vm = VM(instructions)
screen=defaultdict(int)
score = 0

solveVm, solveScreen, _, _ = play_ahead(vm, screen, useCopy = True)
snapshot(solveScreen, printToScreen = True, saveAnimation = False, reset=True)
xMax = max(x for x,y in solveScreen.keys() if solveScreen[x,y] == 0)
xMin = min(x for x,y in solveScreen.keys() if solveScreen[x,y] == 0)
# import cProfile
# cProfile.run("recurSolve(solveVm, solveScreen)")
try:
    recurSolve(solveVm, solveScreen)
except KeyboardInterrupt:
    print("\nInterrupted…\n")

if bestMoves is None:
    print("Did not find any winning move sequence…")
    sys.exit()

print("Found best series of moves: (", bestStepNum, " steps):\n", bestMoves)

vm.input = bestMoves
while vm.is_running:
    try:
        x, y, id = next(vm.run()), next(vm.run()), next(vm.run())
        if x == -1 and y == 0:
            score = id
        else:
            screen[x,y] = id
        if id == 4:
            snapshot(screen, False, True)
    except StopIteration:
        print("###################\n     YOU WIN!\n\n Final Score:", score, "\n###################\n")
        break
    except NeedInputException:
        vm.input = [0]

saveAnimatedGIF(tileSize = 10, duration=1)


